      out.write("function initUserTable(){\r\n");
      out.write("\t$(\"#userTable\").bootstrapTable({\r\n");
      out.write("\t\turl:getRootPath()+'/user/get/list.do',\r\n");
      out.write("\t\tmethod : 'GET',\r\n");
      out.write("\t\tdataType : 'json',\r\n");
