      out.write("\t\tpageSize : 10, //页面容量\r\n");
      out.write("\t\tpageList:[10,20,30,40,'All'],\r\n");
      out.write("\t\tuniqueId : \"id\", //每一行的唯一标识，一般为主键列\r\n");
      out.write("\t\tcardView : false, //是否显示详细视图\r\n");
      out.write("\t\tdetailView : true, //是否显示父子表\r\n");
      out.write("\t\tqueryParams : function(params) {//得到查询的参数\r\n");
      out.write("\t\t\t//这里的键的名字和控制器的变量名必须一直，这边改动，控制器也需要改成一样的\r\n");
      out.write("\t\t\tvar temp = {\r\n");
      out.write("\t\t\t\tlimit : params.limit, // 每页显示数量\r\n");
      out.write("\t\t\t\toffset : params.offset, // SQL语句起始索引\r\n");
      out.write("\t\t\t\tpage : (params.offset / params.limit) + 1,\r\n");
      out.write("\t\t\t\tuserName:$(\"#userName\").val(),\r\n");
      out.write("\t\t\t};\r\n");
      out.write("\t\t\treturn temp;\r\n");
      out.write("\t\t},\r\n");
      out.write("\t\tcolumns:[\r\n");
      out.write("\t\t\t{radio : true},\r\n");
      out.write("\t\t\t{field : 'id',visible : false},//visible:false隐藏\r\n");
      out.write("\t\t\t{field : 'nickName',title : '用户姓名',titleTooltip : '这是用户的名称',align : 'center'},\r\n");
      out.write("\t\t\t{field : 'sex',title : '性别',align : 'center',formatter : function(value,row,index){\r\n");
      out.write("\t\t\t\tif(value == 1) return \"男\";\r\n");
      out.write("\t\t\t\tif(value == 0) return \"女\";\r\n");
      out.write("\t\t\t}},\r\n");
      out.write("\t\t\t{field : 'username',title : '用户名',align : 'center'},\r\n");
      out.write("\t\t\t{field : 'password',title : '密码',align : 'center'},\r\n");
      out.write("\t\t\t{field : 'levelName',title : '所在部门',align : 'center'},\r\n");
      out.write("\t\t\t{field : 'identityName',title : '所属身份',align : 'center'},\r\n");
      out.write("\t\t\t{field : 'cord',title : '身份证',align : 'center'},\r\n");
      out.write("\t\t\t{field : 'telephoneA',title : '常用电话',align : 'center'},\r\n");
      out.write("\t\t\t{field : 'telephoneB',title : '备用电话',align : 'center'},\r\n");
      out.write("\t\t\t{field : 'prohibition',title : '启禁状态',align : 'center', formatter : function(value,row,index){\r\n");
      out.write("\t\t\t\tif (value == 0) return \"<font color='red'>已禁用</font>\";\r\n");
