      out.write("\t\t\t\telse return \"<font color='yellow'>数据加载异常</font>\";\r\n");
      out.write("\t\t\t}}\r\n");
      out.write("\t\t],\r\n");
      out.write("\t\t//注册加载子表的事件。你可以理解为点击父表中+号时触发的事件\r\n");
      out.write("\t\tonExpandRow : function(index, row, $detail) {\r\n");
      out.write("\t\t\t//这一步就是相当于在当前点击列下新创建一个table\r\n");
      out.write("\t\t\tvar parentId = row.id;\r\n");
      out.write("\t\t\tvar cur_table = $detail.html('<table></table>')\r\n");
      out.write("\t\t\t\t\t.find('table');\r\n");
      out.write("\t\t\tvar html = \"\";\r\n");
      out.write("\t\t\thtml += \"<table class='table table-bordered table-condensed'>\";\r\n");
