      out.write("\t\t\t\t\t+ \"</tr>\";\r\n");
      out.write("\t\t\thtml += '</table>';\r\n");
      out.write("\t\t\t$detail.html(html); // 关键地方\r\n");
      out.write("\t\t}\r\n");
      out.write("\t});\r\n");
      out.write("}\r\n");
